mapper.select:
		SELECT * FROM
			(SELECT ROWNUM R, D.* FROM
				(SELECT * FROM POST_VIEW WHERE CATEGORY_NO = ${category_no}


   					AND POST_PUB = 1

				ORDER BY POST_${sort} DESC) D) B
			WHERE B.R BETWEEN ${startRow} AND ${endRow}

SELECT * FROM (SELECT ROWNUM R, D.* FROM (SELECT * FROM POST_VIEW WHERE CATEGORY_NO = ${category_no} AND ${field} LIKE '%' || #{word} || '%'
AND POST_PUB = 1
ORDER BY POST_${sort} DESC) D) B WHERE B.R BETWEEN ${startRow} AND ${endRow}

		SELECT * FROM
			(SELECT ROWNUM R, D.* FROM
				(SELECT * FROM POST_VIEW WHERE USERS_ID = #{users_id} 


   					AND POST_PUB = 1

				ORDER BY POST_NO DESC ) D
			) B WHERE B.R BETWEEN ${startRow} AND ${endRow}

		SELECT * FROM
			(SELECT ROWNUM R, D.* FROM
				(SELECT * FROM POST_VIEW WHERE POST_NO IN (
					SELECT POST_NO FROM REPLY WHERE REPLYS_ID = #{users_id}


   					AND POST_PUB = 1

					GROUP BY POST_NO 
				) ORDER BY POST_NO DESC ) D
			) B WHERE B.R BETWEEN ${startRow} AND ${endRow}

SELECT COUNT(*) FROM POST WHERE POST_NO IN (
	SELECT POST_NO FROM REPLY WHERE REPLYS_ID = #{users_id}


		AND POST_PUB = 1

	GROUP BY POST_NO 
) ORDER BY POST_NO DESC

SELECT * FROM (
	SELECT ROWNUM RA, PA.* FROM (
		SELECT * FROM POST_IMAGE_VIEW ORDER BY POST_${sort} DESC
	) PA WHERE PA.CATEGORY_NO = ${category_no} 


		AND PA.POST_PUB = 1

) PS WHERE PS.RA BETWEEN ${startRow} AND ${endRow}

SELECT * FROM (
	SELECT ROWNUM RA, PA.* FROM (
		SELECT * FROM POST_IMAGE_VIEW ORDER BY POST_${sort} DESC
	) PA WHERE PA.CATEGORY_NO = ${category_no} AND PA.${field} LIKE '%' || #{word} || '%'


		AND PA.POST_PUB = 1

) PS WHERE PS.RA BETWEEN ${startRow} AND ${endRow}

mapper.update:
UPDATE POST
SET POST_TITLE = #{post_title}, POST_CONTENT = #{post_content}
WHERE POST_NO = ${post_no}

mapper.select:
		SELECT * FROM POST WHERE POST_NO = (
			SELECT POST_NO FROM (
				SELECT * FROM POST WHERE USERS_ID = #{users_id} 


   					AND POST_PUB = 1

				ORDER BY POST_DATE
			) WHERE POST_NO > ${post_no} AND ROWNUM = 1
		)

		SELECT * FROM POST WHERE POST_NO = (
			SELECT POST_NO FROM (
				SELECT * FROM POST WHERE USERS_ID = #{users_id} 


   					AND POST_PUB = 1

				ORDER BY POST_DATE DESC 
			) WHERE POST_NO < ${post_no} AND ROWNUM = 1
		)

